const accordionTriggers=document.querySelectorAll(".qaa__question");accordionTriggers.forEach(e=>{e.addEventListener("click",()=>{const t=e.nextElementSibling,i=e.firstElementChild.nextElementSibling;i.classList.toggle("opened"),i.classList.contains("opened")?t.style.maxHeight=t.scrollHeight+"px":t.style.maxHeight=0})});